  function outputPdfToZip($regelId)
  {
    global $__appvar;
    include_once($__appvar["basedir"]."/classes/pclzip.lib.php");
    $zipfile=$__appvar['tempdir']."export.zip";
    $zip=new PclZip($zipfile);
