function HeaderVAR_L72($object)
{
	$pdfObject = &$object;
	$pdfObject->SetFont($pdfObject->rapport_font,'B',$pdfObject->rapport_fontsize);
	$pdfObject->SetWidths(array(79,12,1,16,21,21,25, 5,  20,20,20,20,20));

