include_once($__appvar["basedir"]."/html/rapport/rapportVertaal.php");

class RapportFront_L36
{
	function RapportFront_L36($pdf, $portefeuille, $rapportageDatumVanaf, $rapportageDatum)
	{
		$this->pdf = &$pdf;
		$this->pdf->rapport_type = "FRONT";
		$this->pdf->rapport_datum = db2jul($rapportageDatum);

		if($this->pdf->rapport_FRONT_titel)
			$this->pdf->rapport_titel = $this->pdf->rapport_FRONT_titel;

		$this->portefeuille = $portefeuille;
		$this->rapportageDatumVanaf = $rapportageDatumVanaf;
		$this->rapportageDatumVanafJul=db2jul($this->rapportageDatumVanaf);
		$this->rapportageDatum = $rapportageDatum;
		$this->rapportageDatumJul=db2jul($this->rapportageDatum);
		$this->pdf->extraPage =0;
		$this->DB = new DB();

		$this->rapportMaand 	= date("n",$this->rapportageDatumJul);
		$this->rapportDag 		= date("d",$this->rapportageDatumJul);
		$this->rapportJaar 		= date("Y",$this->rapportageDatumJul);

		$this->pdf->brief_font = $this->pdf->rapport_font;

	}



	function writeRapport()
	{
	  global $__appvar;
    
    /*
    $uitsluiten=array('eMail','portaal','eDossier');
    if(!in_array($this->pdf->selectData['type'],$uitsluiten))
    {
      $this->voorBriefNAW();
		  //$this->voorBrief();
    }
    */
    if($this->pdf->selectData['allInOne']==1)
    {
      $this->pdf->oddEvenCheck[$this->portefeuille]=count($this->pdf->pages);
    }
    else
    {
      if ((count($this->pdf->pages) % 2) <> 0)
	  	{
	      $this->pdf->rapport_type = "BRIEF";
     		$this->pdf->AddPage('L');
        $this->pdf->SetTextColor(254);
        $this->pdf->row(array('','.....'));
        $this->pdf->SetTextColor(0);
        $this->pdf->rapport_type = "FRONT";
	      $this->pdf->frontPage=true;
  	  }
    }
    $this->pdf->last_rapport_type="FRONT";
    $this->pdf->addPage('L');
    $this->pdf->frontPage=true;
    
	  $this->logoWit="";

		$this->pdf->SetFont($this->pdf->rapport_font,'',$this->pdf->rapport_fontsize+14);
		$this->pdf->SetTextColor($this->pdf->rapport_kop_fontcolor['r'],$this->pdf->rapport_kop_fontcolor['g'],$this->pdf->rapport_kop_fontcolor['b']);

    $this->pdf->SetWidths(array(15,250));
    $this->pdf->SetAligns(array('L','L'));
    $this->pdf->SetXY($this->pdf->marge,55);
    $this->pdf->row(array('','Portefeuille '.$this->portefeuille));
    $this->pdf->ln(6);
    $this->pdf->row(array('',$this->pdf->portefeuilledata['Naam']));
    $this->pdf->ln(6);
    $this->pdf->row(array('',$this->pdf->portefeuilledata['Naam1']));
    $this->pdf->ln(12);

    $this->pdf->SetFont($this->pdf->rapport_font,'',$this->pdf->rapport_fontsize+10);

    $this->pdf->SetWidths(array(15,70,100));

    $db=new DB();
    $query="SELECT afkorting FROM Risicoklassen WHERE Risicoklasse='".$this->pdf->portefeuilledata['Risicoklasse']."'";
    $db->SQL($query);
    $risicoklasse=$db->lookupRecord();
    if($risicoklasse['afkorting']<>'')
      $risicoklasse=$risicoklasse['afkorting'];
    else
      $risicoklasse=$this->pdf->portefeuilledata['Risicoklasse'];
    
    
    $this->pdf->row(array('','Profiel portefeuille:',$risicoklasse));
    $this->pdf->ln(5);
    $this->pdf->row(array('','Rapportageperiode:',ceil(date("n",$this->rapportageDatumJul)/3)."e kwartaal ".$this->rapportJaar));
  
$this->pdf->ln(12);
$this->pdf->SetFont($this->pdf->rapport_font,'',$this->pdf->rapport_fontsize+4);
 $this->pdf->row(array('','Relatiemanager:',$this->pdf->portefeuilledata['AccountmanagerNaam']));
 $this->pdf->ln(2);
$this->pdf->row(array('','Telefoon:',$this->pdf->portefeuilledata['VermogensbeheerderTelefoon']));
$this->pdf->ln(2);
$this->pdf->row(array('','Email:',$this->pdf->portefeuilledata['VermogensbeheerderEmail']));
    if(is_file($this->pdf->rapport_logo))
		{
 		    $factor=0.08;
		    $xSize=1400*$factor;
		    $ySize=376*$factor;
   	    $this->pdf->MemImage(base64_decode($this->logoWit), 25, 145, $xSize, $ySize);// $w=0, $h=0, $link=''
		}


	}
  
  function voorBriefNAW()
	{
	  global $__appvar;

		if (count($this->pdf->pages) % 2)
  		$this->pdf->AddPage($this->pdf->CurOrientation);
    $this->pdf->rapport_type = "FACTUUR";
		$this->pdf->AddPage('P');
  	$this->pdf->frontPage = true;

		$font='Arial';
		$fontsize=9;

		$this->pdf->SetFont($font,"",$fontsize);


			  $query = "SELECT
CRM_naw.naam,
CRM_naw.naam1,
CRM_naw.verzendAdres,
CRM_naw.verzendPc,
CRM_naw.verzendPlaats,
CRM_naw.verzendLand,
CRM_naw.verzendAanhef,
CRM_naw.ondernemingsvorm,
CRM_naw.titel,
CRM_naw.voorletters,
CRM_naw.tussenvoegsel,
CRM_naw.achternaam,
CRM_naw.achtervoegsel,
CRM_naw.part_naam,
CRM_naw.part_voorvoegsel,
CRM_naw.part_titel,
CRM_naw.part_voorletters,
CRM_naw.part_tussenvoegsel,
CRM_naw.part_achternaam,
CRM_naw.part_achtervoegsel,
CRM_naw.enOfRekening,
Portefeuilles.BetalingsinfoMee
FROM CRM_naw Join Portefeuilles on CRM_naw.Portefeuille= Portefeuilles.portefeuille WHERE CRM_naw.Portefeuille = '".$this->portefeuille."'  ";

	  $this->DB->SQL($query);
	  $crmData = $this->DB->lookupRecord();
    $extraY=3*$this->pdf->rowHeight;
		$this->pdf->SetY(38+$extraY);
		$this->pdf->SetWidths(array(12,100,100));
		$this->pdf->SetAligns(array("L","L","L"));
		$this->pdf->row(array('','',$crmData['naam']));
		if ($crmData['naam1'] !='')
		  $this->pdf->row(array('','',$crmData['naam1']));
		$this->pdf->row(array('','',$crmData['verzendAdres']));
		$plaats='';
    //listarray($this->waarden);
		if($crmData['verzendPc'] != '')
		  $plaats .= $crmData['verzendPc']." ";
		$plaats .= $crmData['verzendPlaats'];
		$this->pdf->row(array('','',$plaats));



 }
}
