    $txt='';
    if($this->pdf->portefeuilledata['txtKoppeling'] !='')
    {
      $koppeling = stripslashes($this->pdf->portefeuilledata[$this->pdf->portefeuilledata['txtKoppeling']]);
      $koppeling = stripslashes($koppeling);
      $query = "SELECT * FROM custom_txt WHERE
  type = '".$this->pdf->portefeuilledata['txtKoppeling']."' AND
  field = '".$this->pdf->rapport_type."_".$koppeling."' AND
  Vermogensbeheerder = '".$this->pdf->portefeuilledata['Vermogensbeheerder']."'  ";
      $this->DB->SQL($query);
      $txtData = $this->DB->lookupRecord();
      $titel = $txtData['title'];
      $briefData = html_entity_decode(strip_tags($txtData['txt']));
      $txt .= $briefData;//."\n".$this->pdf->portefeuilledata['AccountmanagerNaam'];
    }



    $rowHeightBackup=$this->pdf->rowHeight;
		$this->pdf->rowHeight = 5;
		$this->pdf->SetFont($this->pdf->brief_font, '', 11);
		$this->pdf->SetWidths(array(15, 140));
		$this->pdf->SetAligns(array('L', 'L'));

		$this->pdf->AddPage('P');
    $portefeuilledata=$this->pdf->portefeuilledata;
		$portefeuilledata['tekst'] = $txt;
		$this->printBody($portefeuilledata, false, true);

		$this->pdf->rowHeight = $rowHeightBackup;
  }



	function writeRapport()
	{
		global $__appvar;

    $fontsize = 12; //$this->pdf->rapport_fontsize
		$this->pdf->frontPage = true;
    $this->pdf->SetFont($this->pdf->rapport_font,'',$fontsize);
/*
    if (isset($this->pdf->selectData['type']) && $this->pdf->selectData['type'] != 'eMail')
    {
      // if($this->pdf->selectData['allInOne']==1)

      $this->addBrief();
      $this->pdf->SetFont($this->pdf->rapport_font, '', $fontsize);
    }
*/
    $this->pdf->AddPage('L');

    $this->printBody($this->pdf->portefeuilledata, true, false);

		//        l boven    r boven    r onder    l onder
		$poly=array(15,10,   15+4,10,   15+6,35.5,   15,36) ;
		$this->pdf->Polygon($poly,'DF',null,array($this->pdf->frontBlauw[0],$this->pdf->frontBlauw[1],$this->pdf->frontBlauw[2]));

		//        l boven    r boven    r onder    l onder
		$poly=array(15,70,   15+8,68.8,   15+10,105,   15,106.5) ;
		$this->pdf->Polygon($poly,'DF',null,array($this->pdf->frontBlauw[0],$this->pdf->frontBlauw[1],$this->pdf->frontBlauw[2]));

		//    links onder         rechtes onder      rechts boven        links hk omh     links omhoog      links boven
		$poly=array(15,(210-10), (297-15),(210-10), (297-15),(210-40), (15+15),(210-40), (15+13),(210-70), (15),(210-68) ) ;
		$this->pdf->Polygon($poly,'DF',null,array($this->pdf->frontBlauw[0],$this->pdf->frontBlauw[1],$this->pdf->frontBlauw[2]));

		   $factor=0.05;
		   $xSize=1000*$factor;//$x=885*$factor;
		   $ySize=379*$factor;//$y=849*$factor;
      $logopos=(297/2)-($xSize/2);
      //$logopos=(297)-($xSize)-$this->pdf->marge;
	    $this->pdf->memImage(base64_decode($this->logo), $logopos, 175, $xSize, $ySize);

   	$this->pdf->widthA = array(60,140);
		$this->pdf->alignA = array('L','L','L');






    $this->pdf->SetWidths($this->pdf->widthA);

    $this->pdf->SetY(55);
    $this->pdf->SetFont($this->pdf->rapport_font,'B',$fontsize);
    $this->pdf->row(array(' ',vertaalTekst('PERSOONLIJK EN VERTROUWELIJK',$this->pdf->rapport_taal)));
    $this->pdf->SetFont($this->pdf->rapport_font,'',$fontsize);

		$this->pdf->SetY(100);

		$rapportagePeriode = date("d",$this->rapportageDatumVanafJul)." ".
		                     vertaalTekst($__appvar["Maanden"][date("n",$this->rapportageDatumVanafJul)],$this->pdf->rapport_taal)." ".
		                     date("Y",$this->rapportageDatumVanafJul).
		                     ' '.vertaalTekst('t/m',$this->pdf->rapport_taal).' '.
		                     date("d",$this->rapportageDatumJul)." ".
		                     vertaalTekst($__appvar["Maanden"][date("n",$this->rapportageDatumJul)],$this->pdf->rapport_taal)." ".
		                     date("Y",$this->rapportageDatumJul);

    $this->pdf->SetWidths(array(60,47,5,120));
    $this->pdf->row(array('',vertaalTekst('Verslagperiode',$this->pdf->rapport_taal),":",$rapportagePeriode));
		$this->pdf->ln();
		$this->pdf->row(array(' ',vertaalTekst('Vermogensrapportage',$this->pdf->rapport_taal),':',$this->pdf->portefeuilledata['Portefeuille']));
    $this->pdf->ln();
    $this->pdf->row(array(' ',vertaalTekst('Mandaat',$this->pdf->rapport_taal),':',vertaalTekst($this->pdf->portefeuilledata['Risicoklasse'],$this->pdf->rapport_taal)));
    $this->pdf->ln();
    $this->pdf->row(array('',vertaalTekst('Datum',$this->pdf->rapport_taal),': ',date("j")." ".vertaalTekst($__appvar["Maanden"][date("n")],$this->pdf->rapport_taal)." ".date("Y")));
    $this->pdf->SetWidths($this->pdf->widthA);
	  $this->pdf->SetTextColor($this->pdf->rapport_fontcolor['r'],$this->pdf->rapport_fontcolor['g'],$this->pdf->rapport_fontcolor['b']);


		$this->pdf->frontPage = true;
    $this->pdf->rapport_type = "INHOUD";
	  $this->pdf->rapport_titel = "Inhoudsopgave";//Inhoudsopgave
	  $this->pdf->addPage('L');
	  $this->pdf->templateVars['inhoudsPagina']=$this->pdf->page;
		//$this->pdf->AutoPageBreak=false;
		$tekst="Effecten zijn gewaardeerd op basis van de slotkoersen van de laatste handelsdag van de rapportageperiode. Door gebrek aan handel kan voor bepaalde effecten de getoonde (slot)koers verouderd zijn. Hoewel dit rapport met de grootste zorgvuldigheid is samengesteld, aanvaarden wij geen aansprakelijkheid voor eventuele onjuistheden. \nVoor vragen kunt u contact opnemen via info@stroevelemberger.nl.";
		$y=$this->pdf->getY();
		$this->pdf->setY(178);
		$this->pdf->SetWidths(array(270));
		$this->pdf->SetFont($this->pdf->rapport_font,'',$this->pdf->rapport_fontsize);
		$this->pdf->row(array(vertaalTekst($tekst,$this->pdf->rapport_taal)));
		//$this->pdf->AutoPageBreak=true;
		$this->pdf->setY($y);

	}
