  function SetCol()
  {
    if ($this->curColumn == 0 )
    {
      $x = $this->pdf->marge + $this->curColumn * $this->colWidth;
    }
    else
    {
      $x = $this->centerMarge + $this->curColumn * $this->colWidth;
    }

    $this->pdf->SetLeftMargin($x);
    $this->pdf->SetX($x);
    $this->pdf->SetY($this->y0);
    $this->curColumn++;
  }

  function newBlok()
  {
    if($this->curColumn < 2)
    {
      // Go to next column
      $this->SetCol();

    }
    else
    {
      // Go back to first column
      $this->curColumn = 0;
      $this->SetCol();
      $this->pdf->marge=8;
      $this->pagecount++;
      if($this->pagecount==1)
        $this->pdf->rapport_sub_titel="Samenvatting van de gegevens zoals die zijn vastgelegd in het clintprofiel";
      if($this->pagecount==2)
        $this->pdf->rapport_sub_titel="Huidige situatie";
      $this->pdf->AddPage();
    }
    $this->pdf->SetY($this->y0);
  }

  
