  function printKop($data)
  {
   // $this->pdf->Ln();
   
    if( $this->pdf->GetY() > $this->blokMarginBottom)
    {
      $this->newBlok();
    }

    $h        = (isset($data['h']))?$data['h']:4;
    $align    = (isset($data['align']))?$data['align']:"L";
    $fontsize = (isset($data['fontsize']))?$data['fontsize']:$this->pdf->rapport_fontsize;
    $style    = (isset($data['style']))?$data['style']:"";

    if(isset($data['preLn']))
    {
      $this->pdf->Ln($data['preLn']);
    }
    else
    {
      $this->pdf->Ln(2);
    }

    $this->pdf->SetFont($pdfObject->rapport_font,'B',$fontsize);
    if(isset($data['startY']))
    {
      $this->pdf->SetY($data['startY']);
    }

    $this->pdf->MultiCell($this->colWidth,$h,$data['txt'],0,$align);
    $this->pdf->Ln(2);
    $this->pdf->SetFont($pdfObject->rapport_font,'',$fontsize);

  }
  
  function printRegel($data)
  {

    $h        = (isset($data['h']))?$data['h']:4;
    $align    = (isset($data['align']))?$data['align']:"L";
    $fontsize = (isset($data['fontsize']))?$data['fontsize']:$pdfObject->rapport_fontsize;
    $style    = (isset($data['style']))?$data['style']:"";

    if(isset($data['preLn']))
    {
      $this->pdf->Ln($data['preLn']);
    }

    $this->pdf->SetFont($pdfObject->rapport_font,$style,$fontsize);

    if(isset($data['startY']))
    {
      $this->pdf->SetY($data['startY']);
    }

    $this->pdf->MultiCell($this->colWidth,$h,$data['txt'],0,$align);
  }


  function printCells($data)
  {
    $x=$this->pdf->getX()+3;
    $this->pdf->setX($x);
    
    foreach($data['cells'] as $cell)
    {
      if($cell[3]==1)
      {
        $this->pdf->SetFont($pdfObject->rapport_font,'u',$this->pdf->rapport_fontsize);
      }
      elseif($cell[3]==2)
      {
        $this->pdf->SetFont($pdfObject->rapport_font,'b',$this->pdf->rapport_fontsize);
      }
      else
      {
        $this->pdf->SetFont($pdfObject->rapport_font,'',$this->pdf->rapport_fontsize);
      }
      $this->pdf->Cell($cell[0],5,$cell[2],0,0,$cell[1]);
    }

    if($data['rect'])
    {
      $this->pdf->Rect($x+$data['rect'][0],$this->pdf->GetY()+$data['rect'][1], $data['rect'][2], $data['rect'][3] ,$data['rect'][4],$data['rect'][5],$data['rect'][6]); 
    }
    $this->pdf->Ln();
  }
  
  function printBlok($data)
  {

    $h        = (isset($data['h']))?$data['h']:4;
    $align    = (isset($data['align']))?$data['align']:"L";
    $fontsize = (isset($data['fontsize']))?$data['fontsize']:$this->pdf->rapport_fontsize;
    $style    = (isset($data['style']))?$data['style']:"";

    $this->pdf->SetFont($pdfObject->rapport_font,'',$fontsize);
    if(isset($data['startY']))
    {
      $this->pdf->SetY($data['startY']);
    }

    if(isset($data['preLn']))
    {
      $this->pdf->Ln($data['preLn']);
    }

    $this->pdf->MultiCell($this->colWidth,$h,$data['txt'],0,$align);
    $this->pdf->ln();

    if(isset($data['postLn']))
    {
      $this->pdf->Ln($data['postLn']);
    }

  }

  function printAfbeelding($data)
  {
    $afbeeldingHoogte=80;
    if( $this->pdf->GetY() > 205-$afbeeldingHoogte)
    {
      $this->newBlok();
    }
    $imgData=base64_decode($this->images[$data['naam']]);
    $xLocatie=210/2-$data['width']/2;
    if(isset($data['startY']))
    {
      $startY=$data['startY'];
    }
    else
    {
      $startY=$this->pdf->GetY()+4;
    }

    $this->pdf->MemImage($imgData,$xLocatie,$startY,$data['width']);
     
    $this->pdf->Ln($afbeeldingHoogte);
    
  }

  function checkMarge($blok)
  {
    if($blok['margeY'])
    {
      if($this->pdf->PageBreakTrigger-$this->pdf->getY() < $blok['margeY'])
      {
        $this->newBlok();
      }
    }

    if($blok['ln'])
    {
      $this->pdf->ln($blok['ln']);
    }
  }
  
  function addDocument($document)
  {
    foreach($document as $blok)
    {
       switch ($blok['type'])
       {
        case 'regel':
          $this->printRegel($blok);
        break;
        case 'kop':
          $this->printKop($blok);
        break;
        case 'par':
          $this->printBlok($blok);
        break;     
        case 'afbeelding':
          $this->printAfbeelding($blok);
        break;  
        case 'nextpage':
          $this->newBlok();
        break;
        case 'cells':
          $this->printCells($blok);
        break;
        case 'check':
          $this->checkMarge($blok);
        break;
       }
    }
  }

